mapper.insert:
INSERT INTO ACCOUNT
	(
	NO
	,WRITER_NO
	,ACCOUNT_CNO
	,CONTENT
	,PRICE
	,ACCOUNT_DATE
	)
VALUES 
	(
	SEQ_ACCOUNT_NO.NEXTVAL
	,#{writerNo}
	,#{accountCno}
	,#{content}
	,#{price}
	,#{accountDate}
	)

mapper.update:
UPDATE ACCOUNT SET 
	ACCOUNT_CNO = #{accountCno},
	CONTENT = #{content},
	PRICE = #{price},
	ACCOUNT_DATE = #{accountDate}
WHERE NO= #{no}

UPDATE ACCOUNT SET
STATUS = 'N'
WHERE NO = #{no}
