mapper.select:
SELECT COUNT(NO) FROM FAQ WHERE STATUS = 'N'
AND FAQ_CNO = #{searchType}
AND QUESTION LIKE '%${searchValue}%'

mapper.update:
UPDATE FAQ
SET HIT = HIT + 1
WHERE NO = #{fno}

mapper.insert:
INSERT INTO QNA
(
	NO
	, QNA_CNO
	, MEMBER_NO
	, TITLE
	, CONTENT
) VALUES(
	SEQ_QNA_NO.NEXTVAL
	, #{qnaCno}
	, #{memberNo}
	, #{title}
	, #{content}
)

mapper.delete:
DELETE FROM QNA
WHERE NO = #{no}
AND MEMBER_NO = #{memberNo}

mapper.insert:
		INSERT INTO REPORT 
		(
	        NO
	        ,REPORT_CNO
	        ,REPORTER
	        ,RESPONDENT
	        ,TITLE
	        ,CONTENT
    	) VALUES (
	        SEQ_REPORT_NO.NEXTVAL
	        , #{reportCno}
	        , #{reporter}
	        , #{respondent}
	        , #{title}
	        , #{content}
		)

mapper.delete:
DELETE FROM REPORT
WHERE NO = #{no}
AND REPORTER = #{reporter}

mapper.select:
SELECT COUNT(Q.NO) FROM QNA Q JOIN QNA_CATEGORY C ON Q.QNA_CNO = C.NO JOIN MEMBER M ON Q.MEMBER_NO = M.NO
AND Q.TITLE LIKE '%${searchValue}%'
AND M.NAME LIKE '%${searchValue}%'
AND Q.ENROLL_DATE BETWEEN '${startDate}' AND '${endDate}'
AND Q.ANSWER_YN = #{answerYn}
AND Q.QNA_CNO = #{qnaCno}

mapper.update:
UPDATE QNA SET
    ANSWER_DATE = SYSDATE
    , ANSWER_YN = 'Y'
    , ANSWER = #{answer}
WHERE NO = #{no}

mapper.select:
SELECT COUNT(R.NO) FROM REPORT R JOIN REPORT_CATEGORY C ON R.REPORT_CNO = C.NO JOIN MEMBER M ON R.REPORTER = M.NO
AND R.TITLE LIKE '%${searchValue}%'
AND M.NAME LIKE '%${searchValue}%'
AND R.ENROLL_DATE BETWEEN '${startDate}' AND '${endDate}'
AND R.ANSWER_YN = #{answerYn}
AND R.REPORT_CNO = #{reportCno}

SELECT COUNT(NO) FROM FAQ WHERE STATUS = 'N'
AND FAQ_CNO = ${faqCno}
AND QUESTION LIKE '%${searchValue}%'

mapper.update:
UPDATE FAQ
	SET STATUS ='Y'
WHERE NO = #{fno}

UPDATE FAQ
    SET QUESTION = #{question}
        , ANSWER = #{answer}
        , MODIFY_DATE = SYSDATE
WHERE NO = #{fno}
AND STATUS = 'N'

mapper.insert:
INSERT INTO FAQ
(
    NO
    , WRITER_NO
    , FAQ_CNO
    , QUESTION
    , ANSWER
) VALUES(
    SEQ_FAQ_NO.NEXTVAL
    , #{writerNo}
    , #{faqCno}
    , #{question}
    , #{answer}
)
