mapper.insert:
INSERT INTO NOTICE (
	NO
    ,NOTICE_CNO
    ,WRITER_NO
    ,TITLE
    ,CONTENT

    )
VALUES (
    SEQ_NOTICE_NO.NEXTVAL
    ,#{noticeCno}
    ,#{writerNo}
    ,#{title}
    ,#{content}

    )

mapper.update:
UPDATE NOTICE
	SET
		NOTICE_CNO = #{noticeCno}
	    ,TITLE = #{title}
	    ,CONTENT = #{content}


	WHERE NO = #{no} AND WRITER_NO = #{writerNo}

mapper.delete:
UPDATE NOTICE 
	SET STATUS = 'N'
WHERE NO = #{no}
AND WRITER_NO = #{writerNo}
